mapper.select:
select NVL(SUM(p_price * c_count),0) money
from campus_cart c, campus_product p
where c.p_number = p.p_number and c.u_userid = #{u_userid}

mapper.update:
update campus_cart
set c_count = #{c_count}
where u_userid = #{u_userid}
and p_name = #{p_name}

update campus_cart
set c_count = #{c_count}
where u_userid = #{u_userid}
and c_cartnumber = #{c_cartnumber}
